mapper.select:
select

from tx_dest
where 1 = 1

  and tx_hash = #{txHash,jdbcType=VARCHAR}


  and symbol = #{symbol,jdbcType=VARCHAR}

mapper.delete:
delete from tx_dest
where 1 = 1

  and tx_hash = #{txHash,jdbcType=VARCHAR}


  and symbol = #{symbol,jdbcType=VARCHAR}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into tx_dest (tx_hash, address, amount, 
  symbol, create_time, update_time
  )
values (#{txHash,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, #{amount,jdbcType=VARCHAR}, 
  #{symbol,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
